mapper.update:
UPDATE BOARD
   SET BTITLE = #{btitle},
       BCONTENT = #{bcontent},
       BDATE = SYSDATE,
       IID = #{iid}
 WHERE BNO = #{bno}
   AND MID = #{mid}

mapper.delete:
 		DELETE BOARD
		 WHERE BNO = #{bno}
		   AND MID = #{mid}

mapper.select:
 		SELECT BNO, BTITLE, BCONTENT, BDATE, MID, BHITCOUNT, IID
		  FROM BOARD
		 WHERE BNO = #{bno}
